function validateFieldLimits() {
	if ((!vaInputs.show_text_input_question.isValueSet() || vaInputs.show_text_input_question.getValue().length == 0)
	 && !vaInputs.show_text_input_question.isRequired())
		return;
	var max = vaVars.currentQuestionMax;
	var min = vaVars.currentQuestionMin;

	var value = vaInputs.show_text_input_question.getValue();
	var newvalue = Number(value);
	if (isNaN(newvalue)) {vaVars.currentAnswerValidation = false;vaVars.currentAnswerErrorMsg = gs.getMessageLang('The input value is not a valid number. Please try again', vaContext.getRequesterLang());
